import Navbar from "./Appbar";
import '../styles/Cart.css'

import axios from "axios";
import { useEffect, useState } from "react";



function Cart() {

    const [items, setItem] = useState([]);

    const fetchData = async () => {
        const userId = localStorage.getItem('id');
        if (userId && userId != -1) {

            const url2 = `http://localhost:8000/user/${userId}`;


            const response = await axios.get(url2);
            const id = response.data.data.user.cart;

            for (let i = 0; i < id.length; i++) {
                let url = `http://localhost:8000/items/${id[i]}`;
                let temp = await axios.get(url)
                temp = temp.data.data.item;
                console.log(temp);
                await setItem([...items, temp])
            }

            console.log(items);

        }
    }



    useEffect(() => {
        fetchData()
    }, [])

    return (
        <>
            <Navbar />
            <div className="cart-container">
                <div className="left cart-child">

                </div>
                <div className="right cart-child">
                    {
                        items.map((item) => (
                            <div className="cart-list">

                            </div>
                        ))
                    }
                </div>

            </div>
        </>
    );

}

export default Cart;
